import React from "react";
import { NavLink } from "react-router-dom";
import { routeDeteil as routeDeteilPage } from "../../../pages/DeteilPage";
import { INews } from "../../../types/INews";
import "./styles.scss";

interface InewsItemParams {
  item: INews;
}

const NewsItem: React.FC<InewsItemParams> = ({ item }) => (
  <NavLink className="wrap-setitem" to={routeDeteilPage(item.show.id)}>
    <div className="about-movis-wrap ">
      <figure>
        {" "}
        <img
          className="poster-movis "
          src={item.show.image.medium}
          alt={"poster"}
        />
        <div className="hover-div">
          <div className="rating-average">
        <div className="rating-average-first">{ item.show.rating.average && item.show.rating.average.toFixed(0)}</div>
          <div className="rating-average-second">{ item.show.rating.average && item.show.rating.average.toString().slice(1)}</div>
          </div>
          <div className="country-code">
         
            {item &&
              item.show &&
              item.show.network &&
              item.show.network.country.code}
          </div>
          <div className="runtime">{item.show.runtime &&  item.show.runtime +  ' мин.' }</div>
        </div>
      </figure>
    </div>

    <div className="about-movis-text">
      <div className="movies-name-old">
        <div className="name-movis">{item.show.name}</div>
        <div className="filmold">
          <p>({item.show.premiered.slice(0, 4)})</p>{" "}
        </div>
      </div>
      <div className="genres-movis">{item.show.genres.join(", ")}</div>
    </div>
  </NavLink>
);
export default NewsItem;
