import { AnyAction } from "redux";
import { ISearch } from "types/ISearch";

const initialState = {
  filmsCategory: [],
};

export interface IStore {
  filmsCategory: ISearch[];
}

const movieReducer = (
  state: IStore = initialState, 
  action: AnyAction
  )  => {
  switch (action.type) {
    case "films/setFilms":
      return { ...state, filmsCategory: [...action.payload] };
    default:

      return state;
  }
};
export default movieReducer;
